mapper.select:
select *
from orders


        and status = #{status}


        and number = #{number}


        and phone = #{phone}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and user_id = #{userId}


order by order_time desc

select o.id, o.number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time,
o.pay_method, o.pay_status, o.amount, o.remark, o.user_name, o.phone, o.address, o.consignee,
o.cancel_reason, o.rejection_reason, o.cancel_time, o.estimated_delivery_time, o.delivery_status,
o.delivery_time, o.pack_amount, o.tableware_number, o.tableware_status,
od.id as order_detail_id, od.name as order_detail_name, od.order_id as order_detail_order_id,
od.dish_id, od.setmeal_id, od.dish_flavor, od.number as order_detail_number, od.amount as order_detail_amount,
od.image as order_detail_image
from orders o
left join order_detail od on o.id = od.order_id


        and o.status = #{status}


        and o.number = #{number}


        and o.phone = #{phone}


        and o.order_time >= #{beginTime}


        and o.order_time <= #{endTime}


        and o.user_id = #{userId}


order by o.order_time desc, od.id
